mapper.select:
select
id, follow_up_group_id, department_id, classification, name, submitter, submission_time, state
from hospital.term_approval


        and id = #{id}


        and follow_up_group_id = #{followUpGroupId}


        and department_id = #{departmentId}


        and classification = #{classification}


        and name = #{name}


        and submitter = #{submitter}


        and submission_time = #{submissionTime}


        and state = #{state}
